function getRealRule(e){
	var myDate = new Date();
	var vl1 = myDate.getYear();        //(2)
	var vl2 = myDate.getFullYear();    //(4,1970-????)
	var vl3 = myDate.getMonth()+1;       //(0-11,01)
	var vl4 = myDate.getDate();        //(1-31)
	var vl5 = myDate.getDay();         //X(0-6,0)
	var vl6 = myDate.getTime();        //(1970.1.1)
	var vl7 = myDate.getHours();       //(0-23)
	var vl8 = myDate.getMinutes();     //(0-59)
	var vl9 = myDate.getSeconds();     //(0-59)
	var vl10 = myDate.getMilliseconds();    //(0-999)
	var vl11 = myDate.toLocaleDateString();     //
	var mytime=myDate.toLocaleTimeString();     //
	var vl12 = myDate.toLocaleString( );        //
	if(vl3<10){
		vl3 = "0"+vl3;
	}
	if(e!=undefined){
		e = e.replace("[YYYY]",vl2);
		e = e.replace("[MM]",vl3);
		e = e.replace("[DD]",vl4);
		e = e.replace("[hh]",vl7);
		e = e.replace("[mm]",vl8);
		e = e.replace("[ss]",vl9);
		e = e.replace("[YY]",vl1);
	}else{
		return "";
	}
	return e;
}

var columnvisible={};
function initRoleColumn(columnFormId){
    var url = '/myehr/form/queryCardFormColumnsWithUserId.action?formId='+columnFormId;
    $.ajax({
        url:url,
        type:'post',
        cache: false,
        async: false,
        contentType:'application/json',
        success:function(datas){
            for(var i=0;i<datas.length;i++){
                if(datas[i].formColumnUsName==1||datas[i].formColumnUsName==2){
                    if(datas[i].formColumnType==1){
                        var entityTablename = datas[i].formEntityTablename;
                        var fieldTablename = datas[i].formFieldTablename;
                        var columnName=entityTablename.replace(/_/g,'')+'_'+fieldTablename;
                        columnvisible[columnName]=true;
                    }else{
                        columnvisible[fieldTablename]=true;
                    }
                }else{
                    if(datas[i].formColumnType==1){
                        var entityTablename = datas[i].formEntityTablename;
                        var fieldTablename = datas[i].formFieldTablename;
                        var columnName=entityTablename.replace(/_/g,'')+'_'+fieldTablename;
                        columnvisible[columnName]=false;
                    }else{
                        columnvisible[fieldTablename]=true;
                    }
                }
            }
        }
    });
}

